const buyNows=document.querySelectorAll(".buy-now"),modalCart=document.querySelector(".modal-add"),buttonOff=modalCart.querySelector(".button-off");for(buyNow of buyNows)buyNow.addEventListener("click",function(t){t.preventDefault(),modalCart.classList.add("modal-cart")});buttonOff.addEventListener("click",function(t){t.preventDefault(),modalCart.classList.remove("modal-cart")});
