const buyNows=document.querySelectorAll(".buy-now"),modalCart=document.querySelector(".modal-add"),closeAdd=modalCart.querySelector(".close-button");for(buyNow of buyNows)buyNow.addEventListener("click",function(e){e.preventDefault(),modalCart.classList.add("modal-cart")});closeAdd.addEventListener("click",function(e){e.preventDefault(),modalCart.classList.remove("modal-cart")});
